mapper.select:
SELECT COUNT(1) > 0
FROM business_profiles
WHERE profile_id = #{profileId}

SELECT profile_id, company_name, industry, company_info, website_url, tax_code, email, phone_number, address, is_approved, status, is_deleted, created_at, updated_at FROM business_profiles WHERE 1=1
AND is_approved = #{isApproved}
AND ( company_name LIKE CONCAT('%', #{search}, '%') OR industry LIKE CONCAT('%', #{search}, '%') OR email LIKE CONCAT('%', #{search}, '%') OR phone_number LIKE CONCAT('%', #{search}, '%') )
ORDER BY profile_id ASC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM business_profiles
AND is_approved = #{isApproved}
AND ( company_name LIKE CONCAT('%', #{search}, '%') OR industry LIKE CONCAT('%', #{search}, '%') OR email LIKE CONCAT('%', #{search}, '%') OR phone_number LIKE CONCAT('%', #{search}, '%') )

SELECT status
FROM business_profiles
WHERE profile_id = #{profileId}

mapper.update:
UPDATE business_profiles
SET status = #{status}
WHERE profile_id = #{profileId}
