mapper.select:
SELECT * FROM request_businesses
WHERE is_deleted = 0 and status = #{status}
ORDER BY send_time DESC

mapper.update:
UPDATE request_businesses
SET status = #{status}
WHERE request_id = #{requestId}
  AND is_deleted = 0

UPDATE request_businesses
SET reason = #{reason}
WHERE request_id = #{requestId}

mapper.select:
SELECT * FROM request_businesses
WHERE is_deleted = 0

    AND status = #{status}


    AND send_time < #{cursor}

ORDER BY send_time DESC
LIMIT #{limit}
