mapper.select:
SELECT COUNT(1)
FROM profile_service.student_profiles
WHERE profile_id = #{id}

SELECT COUNT(1) > 0
FROM student_profiles
WHERE profile_id = #{id}

SELECT profile_id, full_name, major, date_of_birth, address, university, avatar_url, academic_year_start, academic_year_end, phone_number, is_approved, status, is_deleted, created_at, updated_at FROM student_profiles WHERE 1=1
AND (full_name LIKE CONCAT('%', #{search}, '%') OR major LIKE CONCAT('%', #{search}, '%') OR university LIKE CONCAT('%', #{search}, '%'))
AND is_approved = #{isApproved}
ORDER BY created_at ASC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM student_profiles WHERE 1=1
AND (full_name LIKE CONCAT('%', #{search}, '%') OR major LIKE CONCAT('%', #{search}, '%') OR university LIKE CONCAT('%', #{search}, '%'))
AND is_approved = #{isApproved}

SELECT status
FROM student_profiles
WHERE profile_id = #{id}

mapper.update:
UPDATE student_profiles
SET status = #{status}
WHERE profile_id = #{id}
